import { csrfFetch } from './csrf';

const ADD_RESERVATION = "reservation/ADD";
const STORE_RESERVATION  = "reservation/STORE";
const GET_RESERVATION  = "reservation/GET";
const DELETE_RESERVATION  = "reservation/DELETE";

/////////////////// ACTION CREATORS ////////////////////

const addBooking = reservation => ({
    type: ADD_RESERVATION ,
    reservation
});

const newBooking = reservation => ({
    type: STORE_BOOKING,
    reservation
});

const getBooking = (reservation) => ({
    type: GET_BOOKING,
    reservation
});

const deleteBooking = (bookingId) => ({
    type: DELETE_BOOKING,
    bookingId
});

//////////////// THUNK ACTION CREATORS ////////////////////

export const createBooking = reservation => async (dispatch) => {
    const response = await csrfFetch(`/api/reservation`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(reservation)
    });

    if (response.ok) {
        const reservation = await response.json();
        dispatch(addBooking(reservation));
        return reservation;
    }
}

export const storeNewBooking = reservation => async (dispatch) => {
    dispatch(newBooking(reservation))
    return reservation;
}

export const getBookings = (userId) => async (dispatch) => {
    const response = await csrfFetch(`/api/users/${userId}/reservations`);

    if (response.ok) {
        const reservation = await response.json();
        dispatch(getBooking(reservation));
        return reservation;
    };
}

export const cancelBooking = (bookingId) => async (dispatch) => {
    const response = await csrfFetch(`/api/reservations/${bookingId}`, {
        method: "DELETE"
    });

    if (response.ok) {
        const bookingId = await response.json();
        dispatch(deleteBooking(bookingId));
    };
}

/////////////////////// REDUCER //////////////////////////

const initialState = {};

const bookingReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_BOOKING:
            return action.reservation;
        case STORE_BOOKING:
            return action.reservation;
        case GET_BOOKING:
            return action.reservation;
        case DELETE_BOOKING:
            const newState = { ...state }
            delete newState[action.bookingId]
            return newState;
        default:
            return state;
    }
};

export default bookingReducer;
