import { csrfFetch } from './csrf';


const FIND = 'reviews/FIND';
const ADD = 'reviews/ADD';
const EDIT = 'reviews/EDIT';
const DELETE = 'reviews/DELETE';


const reviewFind = (reviews, id) => ({
  type: FIND,
  reviews,
  id
});

const add = review => ({
  type: ADD,
  review
});

const edit = change => ({
  type: EDIT,
  change
})

const remove = id => ({
  type: DELETE,
  id
})

export const getReviews = (id) => async dispatch => {
  const res = await csrfFetch(`/api/reviews`,);//${id}`

  if (res.ok) {
    const reviews = await res.json();

    dispatch(reviewFind(reviews, id));
  }
};


export const addReview = (newReview) => async dispatch => {
  const res = await csrfFetch('/api/reviews', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({...newReview})
  });

  if (res.ok) {
    const review = await res.json();
   return dispatch(add(review));
  }
};


export const editReview = (update) => async dispatch => {
  console.log("THIS IS THE UPDATE----------------",update)
  const res = await csrfFetch('/api/reviews/edit', {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({...update})
  });

  if (res.ok) {
    const review = await res.json();

    const [change] = review[1];

    return await dispatch(edit([review]));
  }
}

export const deleteReview = ( id ) =>  async dispatch => {
  console.log("AM I IN THE STORE",id)
  const res = await csrfFetch(`/api/reviews/${id}`, {
    method: 'DELETE'
  });

  if(res.ok) {
    dispatch(remove(id))
  }
}


const reviewReducer = (state = {}, action) => {
  switch(action.type) {
    case FIND:
      {
        const allReviews = {};
        action.reviews.forEach(review => {
          allReviews[review.id] = review;
        });
        return allReviews;

      }
    case ADD:
      {
      const newState = {...state}
      newState[action.review.id] = action.review
      return newState;
      }
    case DELETE:
      {
        const newState = { ...state }
        delete newState[action.id]
        return newState
      }
    case EDIT:
      {
        const newState = { ...state,
        [action.change.id]: {
          ...state[action.change.id],
          body: action.change.body
        }
        }
        return newState
      }
    default:
      return state;
  };

}

export default reviewReducer;
