import { csrfFetch } from './csrf';

const LOAD = 'messages/LOAD';
const FIND_ONE = 'messages/FIND_ONE';


const load = messages => ({
  type: LOAD,
  messages
});

const getOne = message => ({
    type: FIND_ONE,
    message
  });

//Get messages from database//////////////////////////////
export const getmessages = () => async dispatch => {
const res = await csrfFetch('/api/messages'); // problem is here?
if (res.ok) {
    const messages = await res.json();
    console.log({messages})
    dispatch(load({messages}));
    }
}

//Get Specific messages from database//I might not need this
export const findmessages = (id) => async dispatch => {
const res = await csrfFetch(`/api/messages/${id}`);

if (res.ok) {
    const message = await res.json();
    dispatch(getOne(message));
}
};
//Edit messages from database//////////////////////////////
export const editlistings = (message) => async dispatch => {
    const res = await csrfFetch(`/api/messages/${message.id}`, {
                method: "PUT",
                body: JSON.stringify(message),
            });

    if (res.ok) {
        const listingId = await res.json();
        dispatch(findmessages(listingId));
    }
    };


const messagesReducer = (state = {}, action) => {
switch(action.type) {
    case LOAD:{
        const allmessages = {};
        action.messages.forEach(message => {
            allmessages[message.id] = message;
        });
        return {...allmessages}
    }

    case FIND_ONE:
        return {...action.message}
    default:
        return state;
};

}

export default messagesReducer;
