// frontend/src/components/Navigation/ProfileButton.js
import React,{ useState } from "react"; // useEffect,
import { useDispatch } from 'react-redux';//, useSelector
import { editReservation,} from '../../store/reservation';//getReviews, deleteReview,

function EditReservationForm ({Id,listingId, toggleState=true}) {

  const dispatch = useDispatch();
  const [checkInDate, setCheckInDate] = useState("");
  const [checkOutDate, setCheckOutDate] = useState("");


  const updatedcheckInDate = (e) => setCheckInDate(e.target.value);
  const updatedcheckOutDate = (e) => setCheckOutDate(e.target.value);


  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      Id,
      listingId,
      checkInDate,
      checkOutDate,
    };
    const editedReservation = await dispatch(editReservation(payload));
    if (editedReservation) {
     setCheckInDate('');
     setCheckOutDate('');

    }
  };

  const handleCancelClick = (e) => {
    e.preventDefault();

  };

  return (
    <section className="edit-form-holder centered middled" hidden={toggleState}>
    <form onSubmit={handleSubmit}>
      <textarea value={review} placeholder='write your review here...' onChange={updateReview}></textarea>
      <input value={checkInDate} type='date' onChange={updatedcheckInDate }></input>
      <input value={checkOutDate} type='date' onChange={updatedcheckOutDate}></input>
      <button type="submit">Submit Review</button>
      <button type="button" onClick={handleCancelClick}>Cancel</button>
    </form>
  </section>
  );
}

export default EditReservationForm;
