// frontend/src/components/Navigation/ProfileButton.js
import React,{ useState } from "react"; // useEffect,
import { useDispatch, useSelector } from 'react-redux';

function Review() { //will probably need to pass id
  const pokeTypes = useSelector(state => state.pokemon.types);
  const dispatch = useDispatch();

  const [review, setReview] = useState("");
  const [cleanliness, setCleanliness] = useState(0);
  const [communication, setCommunication] = useState(0);
  const [checkIn, setCheckIn] = useState(0);
  const [accuracy, setAccuracy] = useState(0);
  const [location, setLocation] = useState(0);
  const [value, setValue] = useState(0);
  const [createdAt, setCreatedAt] = useState(new Date());

  const updateReview = (e) => setReview(e.target.value);
  const updateCleanliness = (e) => setCleanliness(e.target.value);
  const updateCommunication = (e) => setCommunication(e.target.value);
  const updateCheckIn = (e) => setCheckIn(e.target.value);
  const updateAccuracy= (e) => setAccuracy(e.target.value);
  const updateLocation = (e) => setLocation(e.target.value);
  const updateValue = (e) => setValue(e.target.value);
  const updateCreatedAt= (e) => setCreatedAt(e.target.value);

  // useEffect(() => {
  //   dispatch(getPokemonTypes());
  // }, [dispatch]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      // ...pokemon,
      review,
      attack,
      defense,
      imageUrl,
      name,
      type,
      move1,
      move2,
      moves: [move1, move2],
    };

    const updatedPokemon = await dispatch(updatePokemon(payload));
    if (updatedPokemon) {
      hideForm();
    }
  };

  const handleCancelClick = (e) => {
    e.preventDefault();
    hideForm();
  };

  return (
    <section className="edit-form-holder centered middled">
    <form onSubmit={handleSubmit}>
      <textarea value={userReview} placeholder='write your review here...' onChange={updateReview}></textarea>

      <button type="submit">Update Pokemon</button>
      <button type="button" onClick={handleCancelClick}>Cancel</button>
    </form>
  </section>
  );
}

export default Review;
