// frontend/src/components/Navigation/ProfileButton.js
import React,{ useState, useEffect} from "react"; //
import { useDispatch, useSelector } from 'react-redux';
import { addReview } from '../../store/reviews';
import { getmessages } from '../../store/message';

function Messages ({hostId}) { //will probably need to pass id userId,
  const dispatch = useDispatch();

  const [message, setMessage] = useState("Please Enter Your Message Here");
  const updateMessage = (e) => setMessage(e.target.value);
  const {user} = useSelector(state => state.session);
  const MessageState= useSelector(state => state.messages);
  const messages = Object.values(MessageState);
console.log("where",messages)
    const hostmessages= messages.filter((el) => (el.userId === user.id))
    const usermessages= messages.filter((el) => (el.hostId === hostId))

useEffect(() => {
    dispatch(getmessages());
    }, [dispatch])


  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      userId:user.id,
      hostId:hostId,
      message
    };

    const addedReview = await dispatch(addReview(payload));
    if (addedReview) {
      setMessage('');

    }
  };

  const handleCancelClick = (e) => {
    e.preventDefault();

  };

  return (
    <section className="messages">
        THIS IS MESSAGES
        <div>
            {usermessages?.map((el, i) => <div  id={`message-${i}`} key={i} >user{el.message}</div>)}
        </div>
        <div>
            {hostmessages?.map((el, i) => <div  id={`message-${i}`} key={i} >host{el.message}</div>)}
        </div>
        <form onSubmit={handleSubmit}>
        <textarea value={message} placeholder='write your review here...' onChange={updateMessage}></textarea>
                <button type="submit">Enter</button>
        <button type="button" onClick={handleCancelClick}>Cancel</button>
      </form>
    </section>
  );
}

export default Messages;
